<style>
    *{
        margin: 0;
    }
    .email{
        font-family: sans-serif;

    }
    /* Header */
    .email__header{
        padding: 15px 0;
        box-shadow: rgba(0, 0, 0, 0.16) 0px 1px 4px;
        margin-bottom: 16px;
    }
    .email__header img{
        max-width: 200px;
        display: block;
        margin: 0 auto;
    }

    /* Caixa */
    .box{
        margin-top: 32px;
        padding: 12px 10px;
        border-radius: 12px;
        max-width: 500px;
        margin: 0 auto;
        box-shadow: 0px 17px 20px 1px rgba(86, 86, 86, 0.642);
    }
    .box div{
        margin: 10px 0;
    }
    p{
        margin: 0;
        margin-top: 7px;
    }
    svg{
        display: block;
        margin: 0 auto;
    }
</style>
